INSERT INTO "help_category_m" VALUES(1,'ホーム',0);
INSERT INTO "help_category_m" VALUES(2,'ストーリー',1);
INSERT INTO "help_category_m" VALUES(3,'部員',1);
INSERT INTO "help_category_m" VALUES(4,'ライブ',1);
INSERT INTO "help_category_m" VALUES(5,'勧誘',1);
INSERT INTO "help_category_m" VALUES(6,'ショップ',1);
INSERT INTO "help_category_m" VALUES(7,'その他',0);
INSERT INTO "help_category_m" VALUES(8,'ライセンス',0);
INSERT INTO "help_m" VALUES(1,1,'EXP','EXPはライブが成功するともらえるポイントです。\nEXPのゲージがMAXになるとRankが上がります。');
INSERT INTO "help_m" VALUES(2,1,'Rank','EXPのゲージがMAXになるとRankが上がります。\nRankが上がるとLPが回復して\nLPや友達の上限が増えます。\n※回復するLP量は、\n　Rankが上がったあとの最大LP分となります。');
INSERT INTO "help_m" VALUES(3,1,'LP','ライブをプレイする時に必要なポイントです。\nLPが足りないとライブができないため注意してください。\n\nLPは6分経過するごとに1回復します。\nラブカストーンやLP回復アイテムを使うと、\nすぐに回復することができます。\n※ラブカストーンで回復するLP量は、\n　その時のRankの最大LP分となります。');
INSERT INTO "help_m" VALUES(4,1,'G','部員の練習や特別練習の時に必要です。\n\nライブの報酬や、ログインボーナス\nいろいろなタイミングでもらうことができます。');
INSERT INTO "help_m" VALUES(5,1,'ラブカストーン','勧誘（特待生／限定）、LP回復、コンティニューの他に\n部員枠拡張などをする時に使用することができます。\n\nなお、ラブカストーンは『ショップ』で購入することができます。\n\nまた、Gと同じように、イベントの報酬や、ログインボーナスなど\nいろいろなタイミングでもらうことができます。');
INSERT INTO "help_m" VALUES(6,1,'ラブカストーンピース','一定数集めると「ラブカストーン」と交換される特別なアイテムです。\n\nスペシャルごほうびBOXから一定確率で出現し、\n一定数集まると自動的にラブカストーンへの交換が行われます。\n\n交換に必要なラブカストーンピースの数は、\n交換を重ねるごとに増加し、毎月1日にリセットされます。\n\n※所持しているラブカストーンピースの数はリセットされません。\n※「課題」の「その他」カテゴリーから交換に必要な数や\n　現在所持しているラブカストーンピースの数を\n　確認することができます。');
INSERT INTO "help_m" VALUES(7,1,'ナビゲーター','『ホーム』の左であなたをナビゲートしてくれる部員です。\n\n部員一覧でナビゲーターに設定したい部員をタップし、\n「パートナーにする」を選択すると登場します。\nほかの部員もきてくれることがあります。');
INSERT INTO "help_m" VALUES(8,1,'ログインボーナス','アプリを起動すると、1日1回ログインボーナスがもらえます。\nその日に指定されたボーナスを獲得できます。\n\n※ログインボーナスは毎日AM0:00に更新されます。');
INSERT INTO "help_m" VALUES(9,1,'プレゼントボックス','プレゼントをもらったりするとこちらに届きます。\nプレゼントには受取期限があるため注意してください。');
INSERT INTO "help_m" VALUES(10,1,'おまかせ転部','おまかせ転部では、プレゼントボックス内の\n特定条件のレアリティNの部員を\nまとめて転部することができます。\n条件は以下の通りです。\n\n・サイドストーリー開放済み\n・サポートメンバーではない');
INSERT INTO "help_m" VALUES(11,1,'受取履歴','今まで受取ったプレゼントの履歴を確認できます。\n\n※確認できる期間は受取りから2週間までとなります。');
INSERT INTO "help_m" VALUES(12,1,'一括受取','プレゼントをまとめて受取ることができます。\n表示順に上から1000件までまとめて受取れます。\n一括で受取れなかった時はプレゼントボックスに残ります。\n');
INSERT INTO "help_m" VALUES(13,1,'未受取一覧','まだ受取っていないプレゼントを確認することができます。\n部員数の上限を超えた時にやってきた部員もこちらにいます。');
INSERT INTO "help_m" VALUES(14,1,'新着情報','新しい情報を見ることができます。');
INSERT INTO "help_m" VALUES(15,1,'友達の動き','友達の新しい情報を確認できます。');
INSERT INTO "help_m" VALUES(16,1,'メールリスト','ここにあなた宛てのメールが届きます。');
INSERT INTO "help_m" VALUES(17,1,'送信履歴','あなたが送信したメールを見ることができます。');
INSERT INTO "help_m" VALUES(18,1,'友達','ライブの時にゲストとしてあなたをサポートしてくれます。');
INSERT INTO "help_m" VALUES(19,1,'友達リスト','友達になった学院生のプロフィールをチェックしたり\nメールを送ったりすることができます。\n友達リストの整理もここでできます。\n\n友達リストの右上にあるプルダウンメニューから、\n並べ替えて見ることもできます。');
INSERT INTO "help_m" VALUES(20,1,'申請中リスト','あなたが友達申請をしている学院生を見ることができます。');
INSERT INTO "help_m" VALUES(21,1,'承認待ちリスト','あなたに友達申請をしてくれている学院生を見ることができます。');
INSERT INTO "help_m" VALUES(22,1,'ID検索','IDを入力して友達を見つけることができます。\nあなたのIDもここで確認することができます。');
INSERT INTO "help_m" VALUES(23,1,'課題','ホームの右中央にある課題ボタンから利用できます。\n\n課題を達成することで\nストーリーやライブの楽曲が解禁されたり、\n部員数の上限が増えるなどの\n報酬が獲得できます。');
INSERT INTO "help_m" VALUES(24,1,'イベント','期間限定で特別なライブができます。\n詳しくは『ホーム』右下のイベントバナーをタップして\nイベントページから『ルール説明』を読んでください。');
INSERT INTO "help_m" VALUES(25,1,'お知らせ','アップデート情報やメンテナンス情報などの\n事務局からのお知らせを確認できます。');
INSERT INTO "help_m" VALUES(26,1,'うでまえクラス','うでまえクラスは、Rank30から挑戦することができます。\nSCOREは影響せず、COMBOやタップ評価などの結果をもとに\n自分のうでまえを評価するシステムです。\n\nそれぞれのクラスに応じて試験が用意されており、\nクラスによっていくつかの試験をクリアすれば昇格できるもの\n全ての試験をクリアしなければ昇格できないものと\n様々な条件があります。\n\n現在、うでまえクラスでは\n「ロイヤルエキスパート」まで上がることができます。\nそれより先のクラスについては、\n今後のアップデートで追加する予定です。');
INSERT INTO "help_m" VALUES(27,1,'うでまえクラスの通常試験','うでまえクラスの通常試験は\nライブプレイ時の結果が条件を満たしている場合に\n達成することができます。\n\n通常試験は以下のライブプレイ時に有効となります。\n\n・通常楽曲\n・特別楽曲\n・ランダムセレクト\n・MASTER楽曲プレイ機能\n・スコアマッチ\n・メドレーフェスティバル\n・チャレンジフェスティバル\n・なかよしマッチ\n・おさんぽラリー');
INSERT INTO "help_m" VALUES(28,1,'うでまえクラスの認定試験','うでまえクラスの認定試験は\nホーム＞うでまえクラス画面から挑戦することができます。\n\n通常試験と違い、\n指定の挑戦回数で全ての試験をクリアしなければいけません。\n\n認定試験には次の2種類があります。\n\n・1曲ごとに条件を満たしてクリアしていくもの\n・連続で楽曲をプレイし、最終的に条件を満たす必要があるもの\n\n認定試験に挑戦する際のユニットは\n全て試験ごとに設定されたユニットを使用することになり、\n自分のユニットを使うことができません。');
INSERT INTO "help_m" VALUES(29,1,'認定試験の挑戦回数','認定試験の挑戦回数は一定期間ごとにリセットされ、\n再度挑戦を行えるようになります。\n挑戦回数のリセット期間については以下の3種類あります。\n\n・毎日0:00にリセット\n・毎週月曜0:00にリセット\n・毎月1日0:00にリセット\n\n※リセットタイミングで、途中だった試験の内容も\n　リセットされます。\n※クリア済みの課題はリセットされません。');
INSERT INTO "help_m" VALUES(30,1,'うでまえクラス称号について','自分のうでまえクラスを表す称号は、\n常に自分のいるクラスの称号が表示されます。\nうでまえクラスの称号は称号画面より設定することが可能です。\n\n※自分が過去に獲得したクラスの称号は表示されません。');
INSERT INTO "help_m" VALUES(31,2,'メインストーリー','部員たちとのストーリーを見ることができます。\nクリアすると新しい楽曲が追加されたり、\n新たな課題が設定されたりします。');
INSERT INTO "help_m" VALUES(32,2,'サイドストーリー','部員たちとのサイドストーリーを見ることができます。\n覚醒した部員との絆がMAXになると解禁されます。\n\n絆はライブで深めることができます。\n\n覚醒や絆について詳しく知りたい場合は\n『ヘルプ』の『部員』に記載されています。');
INSERT INTO "help_m" VALUES(33,2,'その他のストーリー','「その他のストーリー」では以下のストーリーを\n見ることができます。\n\n・過去に解放済みのイベントストーリー\n・位置情報を使用することで獲得できるストーリー\n・課題を達成することで獲得できるストーリー');
INSERT INTO "help_m" VALUES(34,2,'MENUについて','ストーリー再生中、右上にあるMENUボタンを押すと\n早送り、スキップ、オート、ログ表示やウィンドウの\n非表示などの機能一覧が表示されます。');
INSERT INTO "help_m" VALUES(35,2,'早送り','ストーリーを早送りすることができます。\n止めたい時は画面をもう一度タップしてください。');
INSERT INTO "help_m" VALUES(36,2,'スキップ','ストーリーの再生をスキップすることができます。\nスキップすると、ストーリークリアとなります。');
INSERT INTO "help_m" VALUES(37,2,'ログ表示','既に読んだテキストを再度表示できます。\n聞きそびれたセリフをもう一度聞くことができます。');
INSERT INTO "help_m" VALUES(38,2,'オート','自動でストーリーを進めてくれる機能です。\n止めたい時はMENUボタンをタップしてください。');
INSERT INTO "help_m" VALUES(39,2,'ウィンドウ非表示','メッセージの部分を非表示にすることができます。\nもう一度タップするとメッセージが表示されます。');
INSERT INTO "help_m" VALUES(40,2,'μ''s・Aqours切り替え','μ''sとAqoursのストーリーメニューを切り替えます。\n切り替えると、ライブや勧誘などにも反映されます。');
INSERT INTO "help_m" VALUES(41,3,'部員について','部活を一緒に頑張っている仲間のことです。\n学校を盛り上げるため、ライブをして転入生を集めています。');
INSERT INTO "help_m" VALUES(42,3,'経験値','部員が練習をするとたまります。\n経験値がたまると、部員のレベルが上がります。');
INSERT INTO "help_m" VALUES(43,3,'レベル','レベルが上がると部員の能力が上がります。');
INSERT INTO "help_m" VALUES(44,3,'絆','ライブをすると部員との絆が深まります。\n絆が深まると部員の能力が上がり、\n覚醒した部員との絆がMAXになると\nその部員のサイドストーリーを読むことができます。\n\nライブでたくさんのCOMBOをつなげると\n部員との絆がさらに深まります。\n\nセンターポジションの部員との絆は\n他の部員よりも深まりやすくなっています。');
INSERT INTO "help_m" VALUES(45,3,'属性','部員の能力には、スマイル、ピュア、クール、全属性の\n4種類があります。\nピンクがスマイル、グリーンがピュア、ブルーがクール\nパープルが全属性です。');
INSERT INTO "help_m" VALUES(46,3,'特技','特技を持っている部員はライブで大活躍します。\n特技を披露してライブが成功しやすくなります。\n\n特技にはLvがあり、\n同じ特技を持つ部員と練習をすると\n特技用の経験値を獲得します。\n経験値がたまると特技Lvがアップします。');
INSERT INTO "help_m" VALUES(47,3,'特技の種類','・SCOREアップ\n　効果量分のSCOREが加算されます。\n\n・体力回復\n　効果量分の体力が回復します。\n\n・判定強化・大\n　一定時間、リズムアイコンのタップ評価GOOD以上を\n　PERFECTにします。\n\n・判定強化・小\n　一定時間、リズムアイコンのタップ評価GREATを\n　PERFECTにします。\n\n・特技発動率アップ\n　一定時間、特技発動率アップを\n　除いた他の特技が発動しやすくなります。\n\n・PERFECT SCOREアップ\n　一定時間、PERFECT時のタップでの獲得SCOREをアップします。\n\n・パラメータアップ\n　一定時間、特技効果で指定した部員の属性Pをアップします。\n\n・特技リピート\n　直前に発動した、特技リピート以外の特技の効果を発動します。\n\n・パラメータシンクロ\n　一定時間、特定の部員と同じ属性Pになることができます。\n\n・特技レベルブースト\n　次に発動する特技は指定された分だけLvがアップした状態で\n　効果を発揮します。\n\n・COMBO FEVER\n　一定時間、達成しているコンボ数に応じてタップSCOREが増えます。');
INSERT INTO "help_m" VALUES(48,3,'特技発動条件','特技には発動条件があり、これを満たした時指定の確率で特技を発動します。\n発動条件には以下のようなものがあります。\n\n・一定数のリズムアイコンの登場\n・一定のコンボ数の達成\n・一定のPERFECT数の達成\n・一定時間の経過\n・一定のスコア獲得\n・スターアイコンPERFECTの達成\n・特定のメンバーの特技発動');
INSERT INTO "help_m" VALUES(49,3,'センタースキル','特別な特技を持っている部員は\nセンターポジションにいると、その能力を発揮させて\nライブが成功しやすくなります。\n\nSSRやURの部員のセンタースキルには、\n特定条件の部員に対して\nさらに能力を発揮させる効果があります。');
INSERT INTO "help_m" VALUES(50,3,'レアリティ','部員のレアリティは全部で5種類あります。\n\n・N（ノーマル）\n・R（レア）\n・SR（スーパーレア）\n・SSR（ダブルスーパーレア）\n・UR（ウルトラレア）\n\n下にいくほどレアリティが高くなります。');
INSERT INTO "help_m" VALUES(51,3,'Type','Typeは部員の種類を分類するものです。\nμ''sとAqoursと虹ヶ咲学園スクールアイドル同好会、\n学年と所属ユニットが設定されています。\n\n部員リストやアルバムではTypeを指定して、\n表示する部員を絞り込むことができます。');
INSERT INTO "help_m" VALUES(52,3,'サポートメンバー','部員の中にはユニットに参加してのライブや\n特別練習（覚醒）ができないサポートメンバーもいます。\n
INSERT INTO "help_m" VALUES(53,3,'ユニット編成','ライブに出演する部員を選んだり、\n入れ替えたりすることができます。\nライブ時のメンバー配置の変更もできます。');
INSERT INTO "help_m" VALUES(54,3,'ユニット数','ユニットは全部で18個作ることができます。\n楽曲に合わせていろんなユニットを作って、\nライブを盛り上げましょう。');
INSERT INTO "help_m" VALUES(55,3,'ユニット名変更','ユニット名を変えることができます。\n好きなユニット名を入力してください。');
INSERT INTO "help_m" VALUES(56,3,'自動編成','部員の属性や特技、Typeの条件を指定して、\nオススメのユニットを組むことができます。\n\n・センターポジションを固定\n　選択したセンターの部員を動かしたくない時や、\n　センターの部員をあとから選びたい時に使います。\n\n・絆ptがMAXになっていない覚醒部員を優先\n　絆ptを貯めたい覚醒済み部員がいる時に使います。\n\n・補正なしのステータスで編成する\n　センタースキルやスクールアイドルスキル、\n　絆ptによるステータス補正を含まずに\n　選びたい時に使います。');
INSERT INTO "help_m" VALUES(57,3,'練習','部員同士で練習をすると部員の経験値が上がります。\n以下の表示が出た場合は、経験値がさらにアップします。\n ・SUPER SUCCESS：経験値1.5倍\n・ULTRA SUCCESS：経験値2倍\n　※特技経験値は対象外となります。\n\n練習をすると、練習に付き合ってくれた部員は\nいなくなるので気を付けて選んでください。\n一部を除くR以上のレアリティの部員を練習相手にした時は\nシールSHOPで利用できるシールを獲得できます。\n\n※一度、練習した部員を戻すことはできません。\n　練習をさせたくない部員については、\n　部員リストから保護していただくことで、\n　誤操作が防げます。');
INSERT INTO "help_m" VALUES(58,3,'特別練習','部員を覚醒させる特別な練習のことです。\nまた、スクールアイドルスキルをセットするための\nスロットも解放されます。\n\n・部員から選ぶ\n　同じ名前の部員でもアルバム詳細の部員Noが同じでないと\n　特別練習ができません。\n　特別練習すると、特別練習に付き合ってくれた部員は\n　いなくなってしまうので、気を付けて選んでください。\n　覚醒した時にスロットが1増加します。\n\n・シールから選ぶ\n　「シール」を使って特別練習を行います。\n　必要なシールの枚数は部員のレアリティによって異なります。\n　シールで覚醒した時は、スロットは増えません。\n\n覚醒済みの部員でも、\nスクールアイドルスキルのスロットを増やすことができる場合は\nさらに特別練習を行うことができます。\n同じ部員同士の場合は2、シールを使った場合は1増加します。');
INSERT INTO "help_m" VALUES(59,3,'覚醒','部員が覚醒をすると、見た目が変わって\nレベルと絆の上限値もアップします。\n\nさらに、覚醒した部員との絆がMAXになると\nその部員のサイドストーリーを読めるようになります。\n※サイドストーリーは後日追加になる場合もございます。');
INSERT INTO "help_m" VALUES(60,3,'転部','部員が転部する時、Gをもらえますが\n部員はいなくなってしまうので注意してください。\n一部を除くR以上のレアリティの部員を転部させた時は\nシールSHOPで利用できるシールを獲得できます。\n\n※一度、転部した部員を戻すことはできません。\n　転部などをさせたくない部員については、\n　部員リストで保護していただくことで、\n　誤操作が防げます。\n\nまた、画面上部の「おまかせ」ボタンをタップすると、\n以下の特定条件を除いたN部員を\nまとめて選択することができます。\n\n・サイドストーリー未解禁\n・保護、パートナーに設定中\n・絆MAX\n・メインユニットに編成中');
INSERT INTO "help_m" VALUES(61,3,'部員リスト','アイドル部に所属している部員を確認できます。\n部員のアイコンをタップすると詳しい情報が確認できます。\n部員リストの右上にあるプルダウンメニューから、\n並び替えて見ることもできます。');
INSERT INTO "help_m" VALUES(62,3,'お気に入り登録','部員の保護をすると\n練習及び特別練習の相手をすることや、転部ができなくなります。\n\nなお、保護は解除することもできます。');
INSERT INTO "help_m" VALUES(63,3,'部員数','部員数が上限を超えると\nそれ以上部員を増やせなくなります。\n\n『ショップ』でラブカストーンを使って増やすことができます。\n\n※ラブカストーンで部員数の上限を増やすことができるのは\n　220回までとなっております。');
INSERT INTO "help_m" VALUES(64,3,'控え室','部室にいるメンバーを控え室に移動することができます。\n\n※控え室に移動できる部員は300人までです。\n※控え室のメンバーは「ユニット編成」「練習」\n　「特別練習」等ができません。');
INSERT INTO "help_m" VALUES(65,4,'ライブって？','いろんな楽曲をプレイすることです。\n部活を頑張る部員たちに指示をだして、\nライブを成功に導きましょう。');
INSERT INTO "help_m" VALUES(66,4,'楽曲','楽曲は通常楽曲と特別楽曲があります。\n難易度を選んで、プレイしたい楽曲をタップしましょう。\n楽曲は、条件を指定して表示する楽曲を並べかえたり、\n絞り込むことができます。\n\n・ランキング\n　楽曲ごとにランキングがあります。\n　SCOREが高い順に表示されています。\n\n・ステージレベル\n　楽曲の難易度を表示しています。\n　星の数字が大きいほど難しくなります。
INSERT INTO "help_m" VALUES(67,4,'通常楽曲','メインストーリーや課題をクリアすると\n遊べるようになる楽曲です。\n難易度EASY・NORMAL・HARD・EXPERTがプレイできます。');
INSERT INTO "help_m" VALUES(68,4,'特別楽曲','期間限定でプレイができる楽曲です。\n\n・日替わり楽曲\n　毎日日替わりで特別な楽曲をプレイできます。\n\n・限定楽曲\n　期間限定の楽曲がプレイできます。\n　期間を過ぎるとプレイできなくなりますので注意してください。');
INSERT INTO "help_m" VALUES(69,4,'MASTER楽曲プレイ機能','「ライブ」メニュー内の上部のボタンからご利用いただけます。\n\nMASTER楽曲プレイ機能は、\nMASTERチャレンジ回数とLPを消費して\nMASTER楽曲を練習することができます。\n※練習できるMASTER楽曲は日によって変わります。\n
INSERT INTO "help_m" VALUES(70,4,'消費LPの変更','ライブ開始前に、消費するLPを変更することができます。\n消費したLPに応じて一部の報酬の量が増加します。\n\n\nアイコンコレクションイベントでは、\n消費するイベントアイコンの数を変更することもできます。\n\n消費LPの変更による報酬の量の変化は以下の通りです。\n\n（増加する報酬）\n・EXP\n・G\n・イベントアイコン\n・イベントpt\n・R部員の出現率\n・ごほうびBOXのゲージ\n\n（増加しない報酬）\n・絆pt\n・お気に入りpt\n・課題の進捗率\n・友情pt\n・楽曲目標のプレイ回数');
INSERT INTO "help_m" VALUES(71,4,'ゲスト','ライブを一緒に手伝ってくれる友達や学院生のことです。\nライブを成功させると、ゲストを選択した時に表示されている\n友情ptがもらえます。\n\nゲストで参加してくれた友達にも、同じように\n友情ptが贈られます。');
INSERT INTO "help_m" VALUES(72,4,'リズムアイコン','画面中央の音符から出てくるアイコンのことです。\n楽曲のリズムにあわせて流れてきます。\n楽曲の難易度が高くなるとリズムアイコンの数が多くなり\n流れてくるスピードが速くなります。\n\n※『ライブ設定』からスピードを調整することができます。');
INSERT INTO "help_m" VALUES(73,4,'ユニットアイコン','ライブに出ている部員たちのことです。\nライブ中の部員の配置を変更したい場合は、\nユニット編成から変更できます。\nリズムアイコンと重なる瞬間にタップしましょう。');
INSERT INTO "help_m" VALUES(74,4,'属性','楽曲にもスマイル、ピュア、クールの3種類の属性があります。\n\nリズムアイコンと部員の属性が一致すると\nSCOREが上がりやすくなります。\n曲の属性に合わせてユニットを編成したり、特技を持った部員を\nユニットに入れたりすることで、ライブがさらに有利になります。');
INSERT INTO "help_m" VALUES(75,4,'部員パラメーター','部員の能力のことです。\n部員詳細で見ることができます。\n\n部員のパラメーターには体力と\nスマイルP、ピュアP、クールP（以下属性P）の\n4つがあります。\nユニットでは9人のパラメーターが合計されます。\n\nライブでは体力は多い方が失敗しにくいです。\nさらに曲には属性があって、曲と同じ属性Pが高ければ高いほど\nライブでSCOREが上がりやすくなります。\n\n部員と絆を深めると、部員の属性Pが上がるので\n絆を深めて、SCOREアップを目指しましょう。\n\nセンタースキルを持っている部員の中には\n属性Pをアップしてくれる部員もいます。');
INSERT INTO "help_m" VALUES(76,4,'タップ評価','ユニットアイコンとリズムアイコンが\n丁度良いタイミングでタップすると、PERFECTになります。\n次に良いのがGREAT、次はGOOD\nこのタイミングでタップできればSCOREがアップします。\n\nタイミングがずれているとBADになり\nタップができないとMISSになります。\n\nBADやMISSだと体力が減りますので気を付けましょう。');
INSERT INTO "help_m" VALUES(77,4,'長押しリズムアイコン','リズムアイコンが白い帯でつながったものは\n「長押しリズムアイコン」と言います。\n\n長押しマークが外れるまで押し続けましょう。\n途中で離したり、離すタイミングが遅いと、\nBADやMISSになってしまうので気を付けましょう。');
INSERT INTO "help_m" VALUES(78,4,'同時押しリズムアイコン','複数のリズムアイコンの真ん中に、\n銀色の横棒が入っているアイコンが\n「同時押しリズムアイコン」です。\n\n部員と重なった瞬間、同時にタップしないと\nBADになるので気を付けましょう。');
INSERT INTO "help_m" VALUES(79,4,'スターアイコン','リズムアイコンの中に\n星のマークが入っているアイコンは\n「スターアイコン」と言います。\n\nPERFECTかGREATのタイミングで\nタップしないと、体力が減ってしまうため気を付けてください。');
INSERT INTO "help_m" VALUES(80,4,'スイングアイコン','リズムアイコンの中に矢印のマークが入っているアイコンは\n「スイングアイコン」と言います。\n始点のアイコンから終点のアイコンまでを\nなぞることで判定されます。\nスイングアイコンは、タップ判定が通常のアイコンより\n広くなっています。\nスイングアイコンの1アイコンで獲得できるスコアは\n通常のリズムアイコンの半分になります。');
INSERT INTO "help_m" VALUES(81,4,'イベントアイコン','イベント開催中に出てくる特別なアイコンです。');
INSERT INTO "help_m" VALUES(82,4,'SCORE','ライブ画面真ん中にある\nあなたがライブで獲得した得点のことです。\n\nタップ評価が良いと上昇しやすくなります。\nSCOREに合わせてSCORE RANKゲージも上昇します。');
INSERT INTO "help_m" VALUES(83,4,'SCORE RANK','SCORE RANKはライブで獲得した\nSCOREの値によって決まります。\n\nS，A，B，Cと4段階あり\nSCOREが良い程、SCORE RANKも良くなります。\n\nSCORE RANKがC以上だと、ボーナスで\n獲得できるGやEXPが増えたりしますので、\nC以上を目指しましょう。\n\nライブ中はSCORE RANKゲージで\n今のSCORE RANKがわかります。');
INSERT INTO "help_m" VALUES(84,4,'SCORE RANKゲージ','ライブ画面上にあるライブのSCORE RANKを\n示すゲージです。\nSCOREが上がるとSCORE RANKゲージも上がります。\n\nライブ中はSCORE RANKゲージで\n今のSCORE RANKがわかります。\n\n途中で体力がなくなってもコンティニューすれば\nSCORE RANKゲージとSCOREはそのままで\nライブを再開することができます。');
INSERT INTO "help_m" VALUES(85,4,'体力','画面左上にある、ライブに参加している部員たちの\n体力のことです。\n\nMISSやBAD、スターアイコンで失敗すると\n体力がへって、体力が全部なくなると、ライブ失敗になります。');
INSERT INTO "help_m" VALUES(86,4,'成功と失敗','ライブが無事成功すると、GやEXPがもらえます。\n\n失敗しても、ラブカストーンを1個使って\nコンティニューすることができます。');
INSERT INTO "help_m" VALUES(87,4,'楽曲目標','楽曲ごとに用意された目標のことです。\n目標はRANK、COMBO、クリア回数の3タイプ\nそれぞれ4つの難易度があります。\n\n見事目標を達成すると、トロフィーマークが現れ、\nそれぞれの報酬を獲得することができます。');
INSERT INTO "help_m" VALUES(88,4,'ライブが成功しない','練習で部員のレベルをあげたり\nライブの属性にあったユニットを編成すると\nライブが成功しやすくなるかもしれません。\n\nライブの属性は、ライブ楽曲選択画面で見ることができます。');
INSERT INTO "help_m" VALUES(89,4,'転入生','ライブをすると、入部希望者がやってきます。\nライブが成功すると、部員になってくれます。');
INSERT INTO "help_m" VALUES(90,4,'ファーストライブボーナス','1日1回、ライブをクリアすることで\nラブカストーンを1個獲得できます。\n※ボーナスは毎日AM0:00にリセットされます。\n\nその日のボーナスを未獲得の場合、メニューバーの『ライブ』に\n『ファーストライブボーナス』のアイコンが表示されます。');
INSERT INTO "help_m" VALUES(91,4,'ごほうびBOX','ごほうびBOXはライブクリア後に出現します。\n獲得したごほうびBOXを開けるには、\nライブをクリアしてゲージをためる必要があります。\nごほうびBOXからは\nスクールアイドルスキルなどのアイテムが獲得できます。\n\nごほうびBOXにはランクがあり、\nランクの高いものほど良いアイテムが出やすい反面、\n開くために必要なゲージも多くなります。\n\nまた、一定確率で「スペシャルごほうびBOX」が\n出現することもあります。\n出現した際は、挑戦中の「ごほうびBOX」と\n「スペシャルごほうびBOX」のどちらに\n挑戦するかを選んで頂きます。\n※挑戦中の通常ごほうびBOXはあとから挑戦可能です。\n\n「スペシャルごほうびBOX」はμ''sやAqours、\nまた各メンバーにちなんだBOXが用意されており、\nメンバー称号などを入手することが可能です。');
INSERT INTO "help_m" VALUES(92,4,'ランダムセレクト','解禁済みの通常楽曲の中から、自動で選曲されるライブです。\n一定数以上の通常楽曲が解禁された後、\n特別楽曲からランダムセレクトをプレイすることができます。\n\nランダムセレクトの楽曲は、\nスマイル、ピュア、クールの楽曲が日替わりで登場します。\n\nランダムセレクトのライブでは、\nごほうびBOXを開けるためのゲージがたまりやすくなります。');
INSERT INTO "help_m" VALUES(93,4,'μ''s・Aqours切り替え','μ''sとAqoursのライブメニューを切り替えます。\n切り替えると、ストーリーや勧誘などにも反映されます。');
INSERT INTO "help_m" VALUES(94,4,'うでまえチェック','以下の画面でリズムアイコンのタップ結果の\n詳細が確認できるようになります。\n\n・「ライブリザルト」画面\n　ライブプレイ時のタップ結果の詳細が表示されます。\n\n・「うでまえチェック」画面\n　楽曲ごとに最もPERFECT数を多く獲得した際の\n　プレイ結果が表示されます。\n　「うでまえチェック」画面へは「ライブ」画面中の\n　青いアイコン(画面左下)から移動可能になります。\n\n※「うでまえチェック」機能は「通常ライブ」と\n　「アイコンコレクション(特殊アイコン収集イベント)」\n　のみの対応となります。');
INSERT INTO "help_m" VALUES(95,4,'ライブリプレイ','「うでまえチェック」画面から\nライブリプレイを見ることができます。\n\nライブプレイを振り返ることができ、画面の上部のグラフで、\n上手くいったところ、ミスをしたところなどが\n確認できるようになっています。');
INSERT INTO "help_m" VALUES(96,5,'特待生勧誘','レアリティがR，SR，SSR，URの\n部員を勧誘することができます。\n\n・1回勧誘\n　ラブカストーン5個で1回勧誘できます。\n\n・11回勧誘\n　ラブカストーン50個で、11回勧誘できます。\n　SR以上の部員を必ず1人勧誘できます。');
INSERT INTO "help_m" VALUES(97,5,'限定勧誘','期間限定で特別な部員を勧誘することができます。\n\n・1回勧誘\n　ラブカストーン5個で1回勧誘できます。\n\n・11回勧誘\n　ラブカストーン50個で、11回勧誘できます。\n　また、SR以上の部員を必ず1人勧誘できます。\n　※BOX勧誘については、SR以上の部員を\n　　必ず1人以上勧誘できる勧誘ではないのでご注意ください。');
INSERT INTO "help_m" VALUES(98,5,'特待生ボーナス','特待生勧誘や限定勧誘を1回するごとに、\n特待生ボーナスが1つたまります。\n特待生ボーナスのゲージがMAXになると\n補助チケットを1枚もらえます。\n\n※補助チケットは直接補助チケット勧誘ページへ付与されます。');
INSERT INTO "help_m" VALUES(99,5,'勧誘チケット','勧誘チケット1枚で1回、特待生勧誘や限定勧誘ができます。\n勧誘チケットは、イベントの上位報酬として\nもらえることがあります。');
INSERT INTO "help_m" VALUES(100,5,'一般生勧誘','友情ptを使って、レアリティがN，R，SSRの部員を勧誘できます。\n1日1回は無料で勧誘できます。');
INSERT INTO "help_m" VALUES(101,5,'友情pt','友情ptは、一般生勧誘をする時に必要なポイントです。\nゲストと一緒にライブをしたり、\n楽曲目標をクリアするとたまります。\n友達からゲストに選択された時ももらうことができます。');
INSERT INTO "help_m" VALUES(102,5,'補助チケット勧誘','補助チケットを使ってできる勧誘のことです。\n\n・SR・UR勧誘\n　補助チケット5枚で\n　SR部員とUR部員のみを勧誘することができます。\n\n・SSR・UR勧誘\n　補助チケット10枚で\n　SSR部員とUR部員のみを勧誘することができます。\n\n・UR勧誘\n　補助チケット25枚で\n　UR部員のみを勧誘することができます。\n\n・特技アップサポートメンバー勧誘\n　補助チケット1枚で特技Lvを上げるのに役立つ\n　特技アップサポートメンバーを勧誘することができます。');
INSERT INTO "help_m" VALUES(103,5,'補助チケット','補助チケットを使って特別な勧誘ができます。\n\n補助チケットはRankが20上がるごとに1枚もらえます\n（プレゼントボックスへ送られます）。\n特待生勧誘や限定勧誘でたまる特待生ボーナスのゲージが\nMAXになった時も、1枚もらえます。\n\n※持っている補助チケットの枚数は\n補助チケットの勧誘ページで確認できます。');
INSERT INTO "help_m" VALUES(104,6,'LP回復','ラブカストーンやアイテムを使って、LPを回復できます。\n\n・ラブカストーン\n　1個でその時のRankの最大LP分のLPを回復できます。\n\n・シュガーキューブ[LP50]\n　1個でLPを50回復できます。\n\n・シュガーポット[LP50％]\n　1個でその時のRankの最大LP50％分のLPを回復できます。\n\n・シュガーポット[LP100％]\n　1個でその時のRankの最大LP分のLPを回復できます。');
INSERT INTO "help_m" VALUES(105,6,'ラブカストーン購入','ラブカストーンを購入することができます。\nまとめ買いするとお得です。\n\nラブカストーンについては\n『ヘルプ』の『ホーム』に詳細が記載されています。');
INSERT INTO "help_m" VALUES(106,6,'部員数の上限を増やす','部員数の上限を増やすことができます。\nラブカストーン1個で4人増やせます。');
INSERT INTO "help_m" VALUES(107,6,'セットアイテムの販売','様々なアイテムが入ったセット商品などを購入できます。\nショップに表示されているバナーをタップすると、\n商品の内容を確認することができます。');
INSERT INTO "help_m" VALUES(108,6,'わくわくボーナス定期券','「わくわくボーナス定期券」は、購入すると\n購入時に受取れるアイテムを含め、\n20日間追加のログインボーナスとして様々なアイテムを\n獲得することができます。\n\n※購入日を含めて30日間有効です。\n　購入後30日以上経過するとログインボーナスの\n　受取状況に関わらず失効いたしますのでご注意ください。\n※「わくわくボーナス定期券」が失効するか、\n　全てのログインボーナスを受取ると再度購入可能になります。\n※「わくわくボーナス定期券」購入後、\n　有効期間中合計20日間ログインすることで\n　全てのアイテムを受取ることができます。\n※ログインボーナスは毎日AM0:00に更新されます。\n※ログイン時に受取れるラブカストーンは、\n　全て無償発行分のアイテムになります。\n※有償および無償発行分のラブカストーンの確認方法は\n　「その他メニュー ＞ 利用規約 ＞ 資金決済法 ＞\n　　未使用残高の確認 ＞ "こちら" のリンク」より確認できます。');
INSERT INTO "help_m" VALUES(109,7,'ライブ設定','ライブに関する各種設定変更ができます。\n\n・アイコン\n　カスタマイズをしたりタイミング調整ができます。\n\n・タップ音\n　タップ音を変更することができます。\n\n・ライブ画面\n　ライブ中演出のON/OFFが設定できます。\n\n・ライブ音量\n　ライブ中のタップ音、特技、楽曲の音量が調整できます。\n\n・ライブスピード\n　各難易度によってスピードが調整できます。\n\n・うでまえチェック\n　機能のON/OFFが設定できます。');
INSERT INTO "help_m" VALUES(110,7,'ライブ画面','・カットイン\n　ライブ中の部員のカットインの設定をすることができます。\n\n・エフェクト\n　ライブ中のエフェクトの設定をすることができます。\n\n・文字サイズ\n　ライブ中のコンボ数や判定の文字の大きさを設定できます。\n\n・タイミング調整\n　ライブ中のタップ反応タイミングを調整することができます。\n\n・リズムアイコン\n　ライブ中に流れるリズムアイコンのデザインを\n　選択することができます。');
INSERT INTO "help_m" VALUES(111,7,'ライブスピード','ライブ中のリズムアイコンが流れてくるスピードを\n難易度ごとに調整することができます。\n\n「デフォルト」ボタンを押すと、\n各難易度標準のスピードに戻ります。');
INSERT INTO "help_m" VALUES(112,7,'アルバム','今まで仲間になったことのある部員を、確認することができます。');
INSERT INTO "help_m" VALUES(113,7,'アルバムの部員詳細','部員の詳しいプロフィールを確認できます。');
INSERT INTO "help_m" VALUES(114,7,'スタンプカスタマイズ','スタンプとはつげんの設定・確認ができます。\n\nスタンプリストとはつげんリストをカスタマイズし、\nマイセットに登録することができます。\nマイセットは5つまで登録可能です。\nマイセットをメインに設定することで、\nイベント中に表示させることができます。\n\nイベントトップからカスタマイズ画面に移動することも可能です。');
INSERT INTO "help_m" VALUES(115,7,'プロフィール','あなたが今までライブを成功させた楽曲の種類や、\nお気に入り部員のランキングを確認できます。\nパートナーに設定したり、\nユニットを組んでライブをしたりすることで\nランキングに表示されるようになります。\n自己紹介を書くこともできます。');
INSERT INTO "help_m" VALUES(116,7,'称号','獲得した称号を確認・設定することができます。\n称号は特定の課題を達成することで獲得することができます。\n\nホーム画面の左上にある称号をタップすることでも\n利用できます。');
INSERT INTO "help_m" VALUES(117,7,'スクールアイドルスキル','セットすることで部員をさらに強化できる特別なスキルです。\nスクールアイドルスキルは\nライブをプレイすることで「ごほうびBOX」から獲得できます。\nそれぞれセットに必要なスロット数が決まっていて、\n強力なものほどたくさんのスロットが必要になります。\n\n部員リストで部員を選択し、\n部員詳細の中の解放済みスロットをタップすると\nスクールアイドルスキルをセットすることができます。');
INSERT INTO "help_m" VALUES(118,7,'ランキング','累計SCOREの個人ランキングが確認できます。\nあなたの順位も確認できます。\n\n※当日AM0:00からのライブプレイにおける\n　合計SCOREが表示されます。\n※前日分のランキングも確認できます。');
INSERT INTO "help_m" VALUES(119,7,'シールSHOP','R以上のレアリティの部員(※)を転部させた時や、\n練習相手に選んだ時に手に入る「シール」を集めて、\n部員やアイテムと交換できます。\n\n※特典として入手可能な部員や\n　アルパカなどのサポートメンバー、\n　限定ログインボーナスで獲得した部員は対象外です。\n※「特別練習」ではシールを獲得することができません。\n\nシールは全部で4種類あり、\n転部対象・練習相手となる部員が\n覚醒しているかどうかにかかわらず1枚獲得できます。\n\n・Uシール\n　URの部員を転部対象・練習相手にすることで獲得できます。\n\n・SSシール\n　SSRの部員を転部対象・練習相手にすることで獲得できます。\n\n・Sシール\n　SRの部員を転部対象・練習相手にすることで獲得できます。\n\n・シール\n　R部員を転部対象・練習相手にすることで獲得できます。');
INSERT INTO "help_m" VALUES(120,7,'背景設定','獲得した背景を確認・設定することができます。\n背景は以下の方法で獲得することができます。\n\n・特定の課題を達成\n・ログインボーナス\n・スペシャルごほうびBOX\n・イベント報酬');
INSERT INTO "help_m" VALUES(122,7,'各種設定','ゲームに関する各種設定変更ができます。\n\n・名前変更\n・誕生日の入力\n・引き継ぎパスワードの入力\n・スクフェスIDの登録\n・テキストの速度変更\n・音量変更\n・データダウンロードに関する設定\n・プッシュ通知のON/OFF');
INSERT INTO "help_m" VALUES(124,7,'アカウント','・名前変更\n　あなたの名前を変更することができます。\n\n・誕生日入力\n　あなたの誕生日を設定することができます。\n　設定すると誕生日当日にパートナーに設定した\n　μ''s・Aqoursメンバーがお祝いをしてくれます。\n　一度登録した情報は変更できません。\n\n・引き継ぎパスワード\n　引き継ぎパスワードを入力することができます。\n\n・スクフェスID\n　設定するとプレイデータの引き継ぎができるようになります。\n　詳しくは『ヘルプ』の『その他』に記載されています。\n');
INSERT INTO "help_m" VALUES(126,7,'引き継ぎパスワード入力','引き継ぎIDとパスワードを引き継ぎ画面で入力することで\nプレイデータを別の端末に引き継ぐことができます。\n\n異なるOS版へ引き継ぐ場合、\nラブカストーンや各種有償の勧誘チケットが\n残っているとデータ引き継ぎができません。\n\n端末を修理に出したり、工場出荷時の設定に戻してしまうと\n今までの記録が消えてしまう場合があるので\n修理や工場出荷時の設定に戻す前に\nスクフェスIDに登録しておきましょう。\n\n※スクフェスIDおよびパスワードの\n　第三者への共有・譲渡は利用規約違反となりますため\n　お控えくださいますようお願い致します。');
INSERT INTO "help_m" VALUES(128,7,'スクフェスID引き継ぎ','①「その他」→「各種設定」→アカウントタブの\n　「スクフェスID」の欄にある「引き継ぐ」を選択\n\n②プレイデータを登録した「スクフェスID」にログイン\n\n③「スクフェスID」へのログイン完了後、「アプリと連携」をタップ\n\n④スクフェス上で引き継ぎ対象の「Rank」と「名前」が\n　表示されるので引き継ぎを実行する場合は「OK」をタップ');
INSERT INTO "help_m" VALUES(129,7,'スクフェスID設定','①「その他」→「各種設定」→アカウントタブの\n　「スクフェスID」の欄にある「登録する」を選択\n\n②プレイデータを登録したい「スクフェスID」にログイン、\n　またはアカウントを新規に登録\n\n③「スクフェスID」へログイン完了後「アプリと連携」をタップ');
INSERT INTO "help_m" VALUES(130,7,'共通','・テキスト速度\n　テキストの表示速度を変更できます。\n\n・BGM\n　BGMの音量を調節できます。\n\n・SE\n　SEの音量を調節できます。\n\n・VOICE\n　部員たちの声の音量を調節できます。\n\n・デフォルト\n　初期状態のサウンドの状態に戻せます。');
INSERT INTO "help_m" VALUES(131,7,'DL','ストーリーや楽曲の追加データをダウンロードできます。\nダウンロードしておくことで\nストーリーや楽曲が解禁された時の\n通信回数を減らし、快適にプレイすることができます。\n後からダウンロードすることもできます。\n\n部員DL設定変更では、\n部員に関するデータのダウンロード方法について\n設定の切り替えができます。');
INSERT INTO "help_m" VALUES(132,7,'プッシュ通知','プッシュ通知とはLPの回復やイベント情報などを\n端末上に表示してお知らせする機能です。\n通知設定は「その他＞各種設定」からいつでも変更できます。\n\n※端末OSで設定をOFFにしていると、\n　ゲーム内でプッシュ通知をONにしていても\n　OFF扱いになります。\n');
INSERT INTO "help_m" VALUES(133,7,'プッシュ通知の種別','プッシュ通知で受取れる内容を選ぶことができます。\n※「LP回復」以外のプッシュ通知は\n　スクフェスからのお知らせがある場合のみ届きます。\n\n・LP回復\n　LPが全回復した時に通知が届きます。\n\n・アップデート情報\n　アップデート情報が届きます。\n\n・キャンペーン\n　特別なキャンペーン情報が届きます。\n\n・楽曲\n　特別楽曲の情報が届きます。\n\n・ログインボーナス\n　期間限定ログインボーナスの情報が届きます。\n\n・イベント\n　イベントの開催情報が届きます。\n\n・勧誘・セット\n　特待生勧誘の部員追加や新セット商品、\n　期間限定勧誘の情報が届きます。\n\n・誕生日\n　メンバー誕生日の情報が届きます。');
INSERT INTO "help_m" VALUES(134,7,'サポート','ゲーム中で起きた不具合に関する報告やご意見・ご要望などの\nゲームに関する問い合わせをする際はこちらをご確認ください。\nなお、頂いたご意見・ご要望についてはすべて確認致しますが、\n原則、返信を行っておりません。\nあらかじめ、ご了承ください。');
INSERT INTO "help_m" VALUES(135,7,'ヒント','ゲームを進める上で、特に大切な情報を確認できます。\nヒントはメインストーリーの進行に合わせて増えていきます。\n\nヒントは『その他』の『ヒント』から\nいつでも確認することができます。');
INSERT INTO "help_m" VALUES(136,7,'利用規約','本サービス利用にあたっての規則です。\nサービス内容、プライバシーポリシー、禁止事項、\n免責事項などの重要な項目が記載されております。\n本サービスを利用することにより、本規約に\n同意することになります。\n記載内容を必ずお読みください。');
INSERT INTO "help_m" VALUES(137,7,'位置情報','特定の場所で端末の位置情報を送信することで\nアイテムが受取れます。\n\n※ご使用端末の位置情報設定をONにしてご利用ください。\n※受取れる場所とアイテムの情報は\n　お知らせにてご確認ください。\n※位置情報の取得により\n　現在位置をトラッキングすることはありません。\n');
INSERT INTO "help_m" VALUES(138,7,'購入履歴','ラブカストーンやアイテムを購入した履歴を、確認できます。');
INSERT INTO "help_m" VALUES(139,7,'アーケード連動','NESiCAを利用して、\nスクフェスアーケード(AC)版と連動することができます。\n\n①サテライト筐体で1度プレイをして\n　NESiCAにプレイヤーデータを保存する\n②サテライト筐体で2プレイ目を開始し、\n　連動確認画面で『スクフェス』の\n　「その他」→「アーケード連動」で表示された\n　QRコードをサテライト筐体に読み込ませる\n\n・1枚のNESiCAにつきスクフェス(スマホ版)\n　1アカウントのみ連動できます\n・連動済みのアカウントを別NESiCAへ連動する場合は、\n　一度スクフェス(スマホ版)内にて「連動解除」してください\n・連動を解除したい場合は、\n　連動済みの「スクフェス(スマホ版)」で\n　「その他」→「アーケード連動」→「連動解除」\n　ボタンをタップしてください');
INSERT INTO "help_m" VALUES(140,7,'ログインボーナス','その月のログインボーナス一覧が確認できます。\nログインボーナスはその日に指定されたボーナスを獲得できます。\nログインした日のマスには「GET」のスタンプがつきます。\n\nその日のログインボーナスが未受取りの場合は、\nここから受取ることもできます。');
INSERT INTO "help_m" VALUES(141,8,'ライセンスについて','このアプリに関するライセンス情報は、<a href="/webview.php/static/index?id=89">こちら</a>をご確認ください。');
